<style>
  /* Layout variables — 3-up across with gap */
  .ugc-wrap{ --gap:12px; --card-w:31%; --radius:18px; margin:8px 0 16px; }
  @media (min-width:900px){ .ugc-wrap{ --card-w:31%; } }

  .ugc-header{ text-align:center; margin:0 0 12px; }
  .ugc-title{ margin:.2rem 0 .35rem; font-size: clamp(1.25rem, 2.2vw, 1.75rem); }

  .ugc-rail-wrap{ position:relative; }
  .ugc-rail{ display:flex; gap:var(--gap); overflow-x:auto; scroll-snap-type:x mandatory; -webkit-overflow-scrolling:touch; padding:4px; scroll-padding:4px; }
  .ugc-rail::-webkit-scrollbar{ display:none; }

  .ugc-card{ flex:0 0 var(--card-w); scroll-snap-align:center; background:#fff; border-radius:var(--radius); box-shadow:0 2px 10px rgba(0,0,0,.06); overflow:hidden; }
  .ugc-video{ position:relative; aspect-ratio:9/16; background:#f2f2f2; }
  .ugc-html5{ width:100%; height:100%; display:block; }

  .ugc-meta{ padding:10px 12px 14px; }
  .ugc-reviewer{ font-weight:600; font-size:.95rem; }

  .ugc-nav{ position:absolute; top:50%; transform:translateY(-50%); border:0; background:rgba(0,0,0,.6); color:#fff; width:36px; height:36px; border-radius:999px; display:none; place-items:center; cursor:pointer; }
  .ugc-prev{ left:4px; }
  .ugc-next{ right:4px; }
  @media (min-width:900px){ .ugc-nav{ display:grid; } }

  /* Mobile: cap video height to ~1/4 of the screen and crop nicely */
  @media (max-width: 899px){
    .ugc-video{ max-height:25vh; }
    .ugc-html5{ object-fit:cover; }
  }
</style>

<script>
  (function(){
    const wrap = document.currentScript.previousElementSibling; // the .ugc-wrap div
    const rail = wrap.querySelector('.ugc-rail');
    const prev = wrap.querySelector('.ugc-prev');
    const next = wrap.querySelector('.ugc-next');

    function gapPx(){
      const g = getComputedStyle(rail).gap;
      return g ? parseFloat(g) : 12;
    }
    function scrollByCard(dir){
      const card = rail.querySelector('.ugc-card');
      const w = card ? card.getBoundingClientRect().width : 300;
      rail.scrollBy({ left: dir*(w + gapPx()), behavior: 'smooth' });
    }
    prev && prev.addEventListener('click', ()=>scrollByCard(-1));
    next && next.addEventListener('click', ()=>scrollByCard(1));

    // Pause other videos when one plays
    const vids = wrap.querySelectorAll('video');
    vids.forEach(v => v.addEventListener('play', () => {
      vids.forEach(o => { if (o !== v) o.pause(); });
    }));
  })();
</script>

