<style>
  /* Sizing: ~2 tiles on mobile, ~5 on desktop */
  .steps-wrap{ --gap:14px; --card-w:46%; --radius:22px; margin:10px 0 18px; }
  @media (min-width:900px){ .steps-wrap{ --card-w:19%; } }

  .steps-title{ font-size:clamp(1.1rem,2.1vw,1.6rem); margin:0 0 12px; }

  .steps-rail-wrap{ position:relative; }
  .steps-rail{
    display:flex; gap:var(--gap);
    overflow-x:auto; scroll-snap-type:x mandatory;
    -webkit-overflow-scrolling:touch; padding:4px; scroll-padding:4px;
  }
  .steps-rail::-webkit-scrollbar{ display:none; }

  .steps-card{
    position:relative; flex:0 0 var(--card-w);
    scroll-snap-align:center; border-radius:var(--radius);
    overflow:hidden; margin:0; background:#f2f2f2;
  }
  .steps-card img{
    width:100%; height:100%; display:block; object-fit:cover;
    aspect-ratio:4/5; /* compact, like the screenshot */
  }

  /* Number badge */
  .steps-badge{
    position:absolute; top:10px; left:10px;
    width:30px; height:30px; border-radius:999px;
    background:#ffffffd9; color:#111; font-weight:700; display:grid; place-items:center;
    font-size:.95rem; z-index:2;
  }

  /* Bottom overlay text (brief) */
  .steps-card figcaption{
    position:absolute; left:0; right:0; bottom:0;
    padding:12px; color:#fff; font-size:.95rem; line-height:1.2;
    background:linear-gradient(180deg, rgba(0,0,0,0) 0%, rgba(0,0,0,.55) 55%, rgba(0,0,0,.75) 100%);
  }

  /* Arrows (desktop only) */
  .steps-nav{
    position:absolute; top:50%; transform:translateY(-50%);
    border:0; background:rgba(0,0,0,.6); color:#fff;
    width:36px; height:36px; border-radius:999px; display:none; place-items:center; cursor:pointer;
  }
  .steps-prev{ left:4px; }
  .steps-next{ right:4px; }
  @media (min-width:900px){ .steps-nav{ display:grid; } }

  /* Compact mobile height tweak */
  @media (max-width:899px){
    .steps-card img{ aspect-ratio:5/6; } /* slightly shorter for tighter fit */
  }
</style>

<script>
  (function(){
    const wrap = document.currentScript.previousElementSibling; // .steps-wrap
    const rail = wrap.querySelector('.steps-rail');
    const prev = wrap.querySelector('.steps-prev');
    const next = wrap.querySelector('.steps-next');

    function gapPx(){
      const g = getComputedStyle(rail).gap;
      return g ? parseFloat(g) : 14;
    }
    function scrollByCard(dir){
      const card = rail.querySelector('.steps-card');
      const w = card ? card.getBoundingClientRect().width : 320;
      rail.scrollBy({ left: dir*(w + gapPx()), behavior: 'smooth' });
    }

    prev && prev.addEventListener('click', ()=>scrollByCard(-1));
    next && next.addEventListener('click', ()=>scrollByCard(1));
  })();
</script>
